import styled, { ServerStyleSheet } from "styled-components";
import React, { useEffect, useState } from 'react'
//import {categories} from "../data"
import { publicRequest } from "../requestMethods";


const Container=styled.div`
display:flex;
padding:20px;
justify-content: space-between;
`;
const sdiv=styled.div`
flex:1;
justify-content:space-between;`;

const adiv=styled.div`
flex:1;
justify-content:space-between;`;


const Category = () => {
    const [Message,setMessage]=useState("")
    const [value,setValue]=useState("")
    const [show,setShow]=useState(false)
    const [responseMessage,setResponse]=useState("")

    useEffect(async()=>{  
       const res=await publicRequest.post(`/user/createmsg`,{message:value})
       setShow(true)
       console.log(res)
       setResponse(res.data.message)
    },[value])



    const showMessage=(e)=>{
        setMessage(e.target.value)
        console.log(Message)
    }
    return (
        <>
        <Container>


           <sdiv>
           <h1>Generate your message  </h1>
           <input type="text" onChange={showMessage}/>
           <button onClick={()=>setValue(Message)}>Ok</button>
           </sdiv>



        </Container>
        <Container>
        <adiv>
           {true && <div>{responseMessage}<button>Edite</button><button>delete</button></div>}
           </adiv>
        </Container>


        </>
    )
}

export default Category
