import styled, { ServerStyleSheet } from "styled-components";
import React, { useEffect, useState } from 'react'
//import {categories} from "../data"
import { publicRequest } from "../requestMethods";
import DataTable from "react-data-table-component"


const Container=styled.div`
display:flex;
padding:20px;
justify-content: space-between;
`;
const sdiv=styled.div`
flex:1;
justify-content:space-between;`;

const adiv=styled.div`
flex:1;
justify-content:space-between;`;


const Category = () => {
    const [Message,setMessage]=useState("")
    const [value,setValue]=useState("")
    const [show,setShow]=useState(false)
    const [responseMessage,setResponse]=useState([])

    useEffect(async()=>{  
       const res=await publicRequest.post(`/user/createmsg`,{message:value})
       setShow(true)
       console.log(res)
       setResponse(res.data.message)
    },[value])



    const showMessage=(e)=>{
        setMessage(e.target.value)
        console.log(Message)
    }

    const data = [{ id: 1, title: 'DataTable in ReactJS', year: '2021' }];
const columns = [
  {
    name: 'Title',
    selector: 'title',
    sortable: true,
  },
  {
    name: 'Year',
    selector: 'year',
    sortable: true,
    right: true,
  },
];


    return (
        <>
        <Container>


           <sdiv>
           <h1>Generate your message  </h1>
           <input type="text" onChange={showMessage}/>
           <button onClick={()=>setValue(Message)}>Ok</button>
           </sdiv>



        </Container>
        <Container>
        <adiv>
           {true && <div>{responseMessage.map((item)=><>{item.message}<button>Edite</button><button>Delete</button></>)}</div>}
           </adiv>
        </Container>
        <datatable title="YourBlogCoach" columns="{columns}" data="{data}"></datatable>

        </>
    )
}

export default Category
