import styled, { ServerStyleSheet } from "styled-components";
import React, { useEffect, useState } from 'react'
//import {categories} from "../data"
import CategoryItem from './CategoryItem';
import { publicRequest } from "../requestMethods";


const Container=styled.div`
display:flex;
padding:20px;
justify-content: space-between;
`;
const sdiv=styled.div`
flex:1;
justify-content:space-between;`;

const adiv=styled.div`
flex:1;
justify-content:space-between;`;


const Category = () => {
    const [Message,setMessage]=useState("")
    const [value,setValue]=useState("")
    const [show,setShow]=useState(false)
    const [responseMessage,setResponse]=useState("")

    useEffect(()=>{  
       const res=publicRequest.post(`/createmsg`,{message:value})
       setShow(true)
       setResponse(res.message)
    },[value])


    const showMessage=(e)=>{
        setMessage(e.target.value)
        console.log(Message)
    }
    return (
        <>
        <Container>


           <sdiv>
           <h1>Generate your message  </h1>
           <input type="text" onChange={showMessage}/>
           <button onClick={()=>setValue(Message)}>Ok</button>
           </sdiv>
           <adiv>
           {true && <div>"hhhh",{responseMessage}<button>Edite</button></div>}
           </adiv>


        </Container>

        </>
    )
}

export default Category
